(62, 'product_reviews_notification', 'A', 'A', NULL, NULL, '{{ company_name }}: {{ __(\"product_reviews.text_new_post_notification\") }}', '{{ snippet(\"header\") }}\n\n  {{ __(\"hello\") }},<br /><br />\n\n{{ __(\"product_reviews.text_new_post_notification\") }}:&nbsp;<a href=\"{{ product_url }}\">{{ product_data.product }}</a>\n<br /><br />\n<b>{{ __(\"person_name\") }}</b>:&nbsp;{% if product_review_data.user_data.name %}{{ product_review_data.user_data.name }}{% else %}{{ __(\"customer\") }}{% endif %}<br />\n\n<b>{{ __(\"product_reviews.rating\") }}</b>:&nbsp;<span style=\"color:#feba0f\">{% if product_review_data.rating_value == \"5\" %}&#9733;&#9733;&#9733;&#9733;&#9733;</span>{% elseif product_review_data.rating_value == \"4\" %}&#9733;&#9733;&#9733;&#9733;&#9734;{% elseif product_review_data.rating_value == \"3\" %}&#9733;&#9733;&#9733;&#9734;&#9734;{% elseif product_review_data.rating_value == \"2\" %}&#9733;&#9733;&#9734;&#9734;&#9734;{% elseif product_review_data.rating_value == \"1\" %}&#9733;&#9734;&#9734;&#9734;&#9734;{% endif %}</span>\n<br />\n\n{% if review_fields == \'advanced\' %}\n    {% if product_review_data.message.advantages %}\n    <b>{{ __(\"product_reviews.advantages\") }}</b>:<br />\n    {{ product_review_data.message.advantages|nl2br }}\n    <br />\n    {% endif %}\n\n    {% if product_review_data.message.disadvantages %}\n    <b>{{ __(\"product_reviews.disadvantages\") }}</b>:<br />\n    {{ product_review_data.message.disadvantages|nl2br }}\n    <br />\n    {% endif %}\n{% endif %}\n\n{% if product_review_data.message.comment %}\n<b>{{ __(\"product_reviews.comment\") }}</b>:<br />\n{{ product_review_data.message.comment|nl2br }}\n<br /><br />\n{% endif %}\n\n{% if product_review_data.status == \'D\' %}\n<b>{{ __(\"product_reviews.text_approval_notice\") }}</b>\n<br />\n{% endif %}\n{{ __(\"view\") }}:<br />\n<a href=\"{{ product_review_url }}\">{{ product_review_url|puny_decode }}</a>\n{{ snippet(\"footer\") }}', NULL, NULL, 'product_r