(63, 'hybrid_auth_create_profile', 'C', 'A', NULL, NULL, '{{ company_name }}: {{ __(\"new_profile_notification\") }}', '{{ snippet(\"header\") }}\n\n{% if user_data.firstname %}\n{{__(\"hello_name\", {\"[name]\" : user_data.firstname})}}\n{% else %}\n{{ __(\"hello\") }},\n{% endif %}\n<br><br>\n{{ __(\"hybrid_auth.password_generated\") }}: {{ user_data.password }}<br> <br />\n{{ __(\"hybrid_auth.change_password\") }}: <br>\n<a href=\"{{ url }}\">{{ url|puny_decode }}</a>\n<br />\n\n{{ snippet(\"footer\") }}', NULL, NULL, 'hybrid_auth', 1639989480, 1639989480);
